mapper.insert:
INSERT INTO TRADE_HISTORY (TRADE_NUM, TRADE_DATE, TRADE_AMOUNT, BALANCE, TRADE_TYPE, ACCOUNT_NUM) VALUES ( TRADE_SEQ.NEXTVAL, SYSDATE, #{trade_amount},
(SELECT BALANCE FROM ACCOUNT_INFO WHERE ACCOUNT_NUM=#{account_num})+#{trade_amount}, #{trade_type}, #{receive_num}
(SELECT BALANCE FROM ACCOUNT_INFO WHERE ACCOUNT_NUM=#{account_num})-#{trade_amount}, #{trade_type}, #{account_num}
)

mapper.update:
  			UPDATE ACCOUNT_INFO SET	

	  			BALANCE = BALANCE + #{trade_amount}
	  			WHERE ACCOUNT_NUM = #{receive_num}


  				BALANCE = BALANCE - #{trade_amount}
	  			WHERE ACCOUNT_NUM = #{account_num}
